package com.leetcode.recursion._explore;
/**
 * Given two strings s and t which consist of only lowercase letters.
 * String t is generated by random shuffling string s and then add one more letter at a random position.
 * Find the letter that was added in t.
 * Example:
 * Input:
 * s = "abcd"
 * t = "abcde"
 * Output:
 * e
 * Explanation:
 * 'e' is the letter that was added.
 */
public class FindStringDifference {
	public char findTheDifference(String s, String t) {
        int[] count= new int['z'-'a'+1];// only lowercase letters
        for(char c: s.toCharArray()) count[c-'a']++;
        for(char c: t.toCharArray()) {
        	count[c-'a']--;
        	if(count[c-'a'] <0) return c;
        }
        return 0;
    }
	public static void main(String[] args) {
		FindStringDifference instance= new FindStringDifference();
		System.out.println(instance.findTheDifference("abcd", "abcde"));
		System.out.println(instance.findTheDifference("a", "aa"));
		System.out.println(instance.findTheDifference("",
""));
	}

}
